<script type="text/javascript">
$(function() {
  function correctLinks() {
    $(".mt-content-connect-leading-text").each(function(i) {
        var readMoreLink = $(".mt-content-connect-readmore").eq(i).find("a").attr('href').substr(3);
        $(this).append("<a href=\"news.html#!/"+readMoreLink+"\" class=\"mt-content-connect-readmore-button\">Read More</a>");
    });
    $(".mt-content-connect-title").each(function(i) {
        var readMoreLink = $(".mt-content-connect-readmore").eq(i).find("a").attr('href').substr(3);
        $(this).wrapInner("<a href=\"news.html#!/"+readMoreLink+"\"></a>");
    });
  }

  setTimeout(correctLinks, 3000);
});
</script>

<script type="text/javascript">
function readMoreLink(link){
  var linkTemplate = '<br><a href="'+link+'" class="read-more-link">READ MORE</a>';
  return linkTemplate;
}

$("li.rss-item").each(function(){
  console.log(readMoreLink($(this).children("a").attr("href")));
  $(this).append(readMoreLink($(this).children("a").attr("href")));
});
</script>

